import { Component, VERSION, ViewChild, ElementRef } from '@angular/core';
import { FormBuilder, FormGroup, UntypedFormGroup, Validators } from '@angular/forms';
import { AutenticacaoService } from '../autenticacao.service';
import { AdministrativoService } from '../administrativo.service';
import { Route, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  hide = true;

  formLogin!: FormGroup;
  formCadastro!: FormGroup;

  constructor(private readonly formBuilder: FormBuilder,
    private authService: AutenticacaoService,
    private adminService: AdministrativoService,
    private route: Router){}

  ngOnInit(): void {
    this.criarFormulario();
  }

  criarFormulario(): void{
    this.formLogin = this.formBuilder.group({
      usuario: ['', [Validators.required]],
      senha: ['', [Validators.required, Validators.minLength(8)]]
    })

    this.formCadastro = this.formBuilder.group({
        nomeCad: ['', [Validators.required]],
        usuarioCad: ['', [Validators.required]],
        senhaCad: ['', [Validators.required, Validators.minLength(8)]],
        senhaConfirma: ['', [Validators.required, Validators.minLength(8)]],
        admnistrativoCad:['', [Validators.required]]
      })
  }

  login(){
    if(!this.formLogin.valid){
      return;
    }
    this.authService.login(this.formLogin.getRawValue()).subscribe(user => {
      this.route.navigate(['home'])
    },(error) => {
      alert('erro ao tentar fazer o login')
    });
  }

  confirma(){
    if(!this.formCadastro.valid){
      return;
    }
    this.adminService.inserir(this.formCadastro.getRawValue()).subscribe(admin => {
        this.route.navigate([''])
    },(error) => {
        alert('erro ao gravar usuario')
      });
    }

    @ViewChild('overlay') overlay!: ElementRef;
    flipToLogin(){
      this.overlay.nativeElement.classList.remove('over-left');
      this.overlay.nativeElement.classList.add('over-right');
    }

    flipToSignup(){
      this.overlay.nativeElement.classList.remove('over-right');
      this.overlay.nativeElement.classList.add('over-left');    
    }

}
<script src="https://unpkg.com/aos@2.3.1/dist/aos.js">
</script>
<script>AOS.init();</script>
<script src="https://code.jquery.com/jquery-3.6.0.min.js" integrity="sha256-/xUj+3OJU5yExlq6GSYGSHk7tPXikynS7ogEvDej/m4=" crossorigin="anonymous"></script>
<script src="/src/app/scripts/scripts.js"></script>
<script src="/src/app/scripts/funcoes.js"></script>
<main>
    <div class="globalContainer">
      <!--FORMULÁRIO DE LOGIN-->
    <div class="sign-in">
        <mat-card formGroup="formLogin">
        <h2 class="sub-title">Login</h2>
        <div>
            <mat-label>Usuario</mat-label>
            <input type="text" autocomplete="nope" name = "usuario" class="input-block" id="id" matInput placeholder="Usuario" formControlName="usuario">
            <mat-label>Senha</mat-label>
            <input class="input-block" name = "senha" matInput [type]="hide ? 'password' : 'text'" formControlName="senha">
            <div class="checkbox">
                <div class="boxes input-center">
                    <input type="checkbox" id="rm-data" class="input-checkbox "><label for="rm-data">Lembrar credenciais.</label>
                </div>
            </div>
            <button type="button" id="logar" class="input-block bg-primary" (click)="login()" [disabled]="!formLogin.valid">Logar</button>
        </div>
        </mat-card>
    </div>
    <div class="sign-up">
        <mat-card formGroup ="formCadastro">
            <h2 class="sub-title">Cadastro</h2>
             <div>
                 <mat-label>Nome</mat-label>
                 <input type="text" name="nomeCad" autocomplete="nope" class="input-block" matInput placeholder="Nome" formControlName="nomeCad" required>
                 <input type="text" name="usuarioCad" autocomplete="nope" class="input-block" matInput placeholder="Usuario" formControlName="usuarioCad" required>
                 <input type="password" name="senhaCad" class="input-block" matInput [type]="hide ? 'password' : 'text'" formControlName="senhaCad" required>
                 <input type="password" name="senhaConfirma" class="input-block" minlength="8" matInput [type]="hide ? 'password' : 'text'" formControlName="senhaConfirma" required>
                 <mat-label>Administrativo</mat-label>

                <div class = "checkbox">
                     <div class="boxes">
                         <input class="input-checkbox" type="radio" name="administrativoCad" id="administrativoCadSim" value = "true" formControlName="administrativoCad" required = "required">Sim
			         </div>
                </div>
                 <div class = "checkbox">
                     <div class="boxes">
                <input class="input-checkbox" type="radio" name="administrativoCad" id="administrativoCadNao" value="false" formControlName="administrativoCad" required = "required">Não
                     </div>
                 </div>
                 <button type="button" id="confirma" class="input-block bg-primary" name="confirma" (click)="confirma()" [disabled]="!formCadastro.valid">Cadastre-se</button>
             </div>
            </mat-card>
    </div>
    <div id="overlay" class="over" #overlay>
            <div class="info">
            <p class="info-text">Bem vindo! Oque deseja fazer?</p>
            <button class="bg-dark text-light" id="flip-to-login" (click)="flipToLogin()">Login</button>
            <button class="bg-primary text-light" id="flip-to-signup" (click)="flipToSignup()">Cadastro</button>
             </div>
    </div>

    </div>
        <div class="alert hide">
            <span class="fas fa-exclamation-circle">!</span>
            <span class="msg">Atenção:</span>
            <span class="close-btn">
                    <span class="fas fa-times">X</span>
            </span>

        </div>
</main>
